INSERT INTO `trv_template` VALUES ('6', 'contract_add2_ONLY_TRANSIT_THERE', 'Приложение 2, если проезд только туда (без проживания)', '<p style=\"text-align: center;\" align=\"center\"><strong>Проезд в : {$city}</strong></p>\r\n<p style=\"text-align: center;\" align=\"center\"><strong>&nbsp;</strong></p>\r\n<p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;<strong>Дата и время убытия из Минска</strong><strong>:</strong><span style=\"text-decoration: underline;\">&nbsp;{$of_date}</span>&nbsp;-&nbsp;ул. Сурганова 61 (бизнес - центр &laquo;Зебра&raquo;) &ndash; в 06.30&nbsp;&nbsp;&nbsp; (время убытия указывается ориентировочно, точное время&nbsp; сообщается за сутки до выезда)</p>\r\n<p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;<strong>Обратный выезд с курорта</strong><strong>:&nbsp;</strong>{$upto_date2}&nbsp; -&nbsp; ориентировочно с&nbsp; 14.00 до 19.00 (точное время отправления согласовывается с руководителем туристической группы в день выезда)</p>\r\n<p>&nbsp;<strong>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;Дата и время прибытия в Минск</strong><strong>:&nbsp;</strong>{$upto_date}&nbsp;<strong>-&nbsp;</strong>18.00 (время прибытия указывается ориентировочно)&nbsp; ул. Дружная</p>\r\n<p>&nbsp; &nbsp; &nbsp; &nbsp;&nbsp;<strong>&nbsp; Питание:&nbsp;</strong>чай, кофе (в дороге)</p>\r\n<p><strong>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;Маршрут:</strong>&nbsp; &nbsp; &nbsp;{$bus_route}</p>\r\n<p><strong>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; Трансфер:</strong>&nbsp; нет</p>\r\n<p><strong>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;</strong><strong><span style=\"text-decoration: underline;\">Программа тура:</span></strong>&nbsp;информационно-консультационные услуги,&nbsp; транспортные услуги.</p>\r\n<p style=\"margin-left: 30px;\"><strong>&nbsp;</strong><span style=\"text-decoration: underline;\"><strong> Примечание:</strong></span> дополнительные&nbsp; услуги&nbsp; оплачиваются туристами по месту отдыха (проживание , питание, экскурсии и т. д.).&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p style=\"text-align: center;\">&nbsp;</p>\r\n<p style=\"text-align: center;\">&nbsp;</p>\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; ОДО &laquo;ИриАнна&raquo;&nbsp;&nbsp;&nbsp;</p>\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<strong>&nbsp;&nbsp;&nbsp;&nbsp;</strong>г. Минск ул. Сурганова, 61,оф.504 &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;{$customer_name}&nbsp;&nbsp;</p>\r\n<p>&nbsp; тел. 292 04 02, факс 388 04 02</p>\r\n<p>&nbsp; р/с 3012014461250,</p>\r\n<p>&nbsp; ф-л № 514 АСБ &laquo;Беларусбанк&raquo;, код 614</p>\r\n<p>&nbsp; УНН 190135143, ОКПО37535316</p>');
