p;</p>\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; ОДО &laquo;ИриАнна&raquo;&nbsp;&nbsp;&nbsp;</p>\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<strong>&nbsp;&nbsp;&nbsp;&nbsp;</strong>г. Минск ул. Сурганова, 61,оф.504 &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;{$customer_name}&nbsp;&nbsp;</p>\r\n<p>&nbsp; тел. 292 04 02, факс 388 04 02</p>\r\n<p>&nbsp; р/с 3012014461250,</p>\r\n<p>&nbsp; ф-л № 514 АСБ &laquo;Беларусбанк&raquo;, код 614</p>\r\n<p>&nbsp; УНН 190135143, ОКПО37535316</p>');

-- ----------------------------
-- Table structure for `trv_tour`
-- ----------------------------
DROP TABLE IF EXISTS `trv_tour`;
CREATE TABLE `trv_tour` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `created` date NOT NULL,
  `manager_id` int(11) NOT NULL,
  `is_only_transit` int(11) DEFAULT NULL,
  `transit` varchar(100) NOT NULL,
  `resort_id` int(11) NOT NULL,
  `hotel_id` int(11) DEFAULT NULL,
  `period_id` int(11) DEFAULT NULL,
  `period_back_id` int(11) DEFAULT NULL,
  `count_of_day` int(11) NOT NULL,
  `bus` int(11) NOT NULL,
  `owner_name` varchar(1000) NOT NULL,
  `owner_tel1` varchar(20) NOT NULL,
  `owner_tel2` varchar(20) NOT NULL,
  `owner_passport` varchar(1000) NOT NULL,
  `owner_birthday` date NOT NULL,
  `owner_travel_service` int(11) NOT NULL,
  `owner_travel_cost` int(11) NOT NULL,
  `count_of_childs` int(11) DEFAULT NULL,
  `ages` varchar(255) DEFAULT NULL,
  `total_travel_service` int(11) NOT NULL,
  `total_travel_cost` int(11) NOT NULL,
  `begin_date` date DEFAULT NULL,
  PRIMARY KEY (`id`)
 
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

-- ----------------------------
-- Records of trv_tour
-- ----------------------------

-- ----------------------------
-- Table structure for `trv_tourists`
-- ----------------------------
DROP TABLE IF EXISTS `trv_tourists`;
CREATE TABLE `trv_tourists` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `owner_id` int(11) NOT NULL,
  `name` varchar(1000) NOT NULL,
  `passport` varchar(1000) NOT NULL,
  `birthday` date NOT NULL,
  `travel_service` int(11) NOT NULL,
  `travel_cost` int(11) NOT NULL,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

-- ----------------------------
-- Records of trv_tourists
-- ----------------------------

-- ----------------------------
-- Table structure for `trv_users`
-- ----------------------------
DROP TABLE IF EXISTS `trv_users`;
CREATE TABLE `trv_users` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `login` varchar(32) NOT NULL,
  `password` varchar(32) NOT NULL,
  `first_name` varchar(32) NOT NULL,
  `second_name` varchar(32) NOT NULL,
  `role_id` int(11) NOT NULL,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=2 DEFAULT CHARSET=utf8;

-- ----------------------------
-- Records of trv_users
-- ----------------------------
